import axios from 'axios'
import React, {useState, useEffect } from 'react'
import toast from 'react-hot-toast'
import TaskItem from '../taskItem/TaskItem'
import "./tasks.css"
const Tasks = () => {

  const [tasks, setTasks] = useState([])
  const [newTask,setNewTask] = useState({})

  const getTasks = async () =>{
    try{
      const {data} = await axios.get('/api/tasks/mytask')
      setTasks(data)
    }catch(err){
      console.log(err)
    }
  }

  useEffect(() => {
		getTasks();
		console.log(tasks);
  }, [newTask]);



const hanleAddTodo = async (e) => {
  e.preventDefault()
  const newToDo = {
    title:e.target.newTask.value
  } 
  console.log(newTask)
  try{
    await axios.post('/api/tasks/', newToDo);
    setNewTask(newToDo);
    toast.success("Task adding completed")
  }catch(err){
    console.log(err);
    toast.error("cannot add task")
  }
};


const handleDeleteTask = async (taskId) => {
  try{
    await axios.delete('')
  }catch(err){
    console.log(err);
	  toast.error('cannot add task');
  }
}

  return (
    <section className="task">
      <div className="task-container">
        <div className="task-title">
          <h2>What's the Plan For Today</h2>
          </div>
          <form onSubmit={hanleAddTodo} className="task-form">
            <label htmlFor="task-newtask">
              <input type="text" name="newTask" placeholder='Add a todo' />
            </label>
            <button type='submit' className="task-form-button">Add Todo</button>
          </form>
        <div className="task-item">
          <TaskItem data = {tasks}/>
        </div>
      </div>

    </section>
  )
}

export default Tasks
