import React from 'react'
import "./navbar.css"
import {useState,useEffect} from 'react'
import axios from 'axios'
import toast from 'react-hot-toast'
import { useNavigate } from 'react-router-dom'

const Navbar = () => {
  const navigate = useNavigate()
  const [user,setUser] = useState({})

  const getUser = async () =>{
    try{
      const userInfo = await axios.get('/api/users/me');
      console.log(userInfo.data)
      setUser(userInfo.data);
    }catch (err){
      console.log(err)
      toast.error("cannot get User Info")
    }

  }

  const handleLogout =  async () => {
    try{
      await axios.post("/api/authen/logout")
      navigate('/auth')
    } catch(err){
      toast.error("Logout fail")
    }
  }

  useEffect(()=>{
    getUser()
  },[])


  return (
		<div className="nav-container grid">
			<div className="nav-logo">
				<h2>TO-DO APP</h2>
			</div>
			<div className="nav-userinfo">
				<i class="bx bxs-user-circle"></i>
				<span>{user.name}</span>
				<span>{user.email}</span>
        <Link> 
        </Link>
				<button type="button" className="nav-button-logout">
					Edit Profile
				</button>
				<button type="button" className="nav-button-logout" onClick={handleLogout}>
					Logout
				</button>
			</div>
		</div>
  );
}

export default Navbar
